import { useState, useEffect } from 'react'

const App = () => {
  const [mobile, setMobile] = useState(false)

  const isMobile = () => {
    return window.innerWidth <= 1024
  }

  useEffect(() => {
    isMobile() ? setMobile(true) : setMobile(false)
  }, [])

  window.addEventListener('resize', () => {
    isMobile() ? setMobile(true) : setMobile(false)
  })

  const styles = {
    titles: {
      t1: mobile ? '50px' : '80px',
      t2: mobile ? '30px' : '50px',
    },
    paragraphs: {
      p1: '20px',
    },
    legends: {
      l1: '20px',
      l2: mobile ? '12px' : '15px',
    },
    palette: {
      greens: {
        g1: '#f1fffb',
        g2: '#d9f9ef',
        g3: '#b2f5df',
        g4: '#72edc4',
        g5: '#00df94',
      },
      grays: {
        g1: '#94A3B8',
        g2: '#E2E8F0',
      },
      blacks: {
        b1: '#000000',
        b2: '#444444',
      },
    },
    paddings: {
      p1: '10%',
      p2: '3%',
    },
  }

  return <></>
}

export default App
